import api from "../utils/Api";

function Main(props) {
    const {onEditProfile, onAddPlace, onEditAvatar} = props;
    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');

    return(
       <main>
        <section className="profile">
                <picture className="profile__avatar" onClick={onEditAvatar}>
                <img alt="Аватар" className="profile__avatar-img" style={{ backgroundImage: `url(${userAvatar})` }}  />
                </picture>
                <div className="profile__info">
                    <div className="profile__description">
                        <div className="profile__line">
                            <h1 className="profile__name">{userName}</h1>
                            <button type="button" className="profile__edit" aria-label="Кнопка редактирования профиля" onClick={onEditProfile}>
                            </button>
                        </div>
                        <p className="profile__job">{userDescription}</p>
                    </div>
                    <button type="button" className="profile__add-button" onClick={onAddPlace}>
                    </button>
                </div>
        </section>

        </main> 
    )
}

export default Main;
