import React from "react";
import {CurrentUserContext} from "../contexts/CurrentUserContext";

function Card(props) {

    const currentUser = React.useContext(CurrentUserContext);

    const isOwn = props.card.owner._id === currentUser._id;

    const cardDeleteButtonClassName = (
        `card__delete-button ${isOwn ? 'card__delete-button_visible' : 'card__delete-button_hidden'}`
      ); 

    const isLiked = props.card.likes.some(i => i._id === currentUser._id);
    const cardLikeButtonClassName = `...`; 

    function handleClick() {
        props.onCardClick(props.card)
    }

    function handleLikeClick() {
        props.onCardLike(props.card)
    }

    return (
        <>
            <article className="element">
                <img className="element__img" alt={props.name} src={props.link} onClick={handleClick} onCardLike={handleLikeClick} />
                <button type="button" className="element__dlt">
                </button>
                <div className="element__group">
                    <h2 className="element__title">{props.name}</h2>
                    <div className="element__group-like">
                        <button type="button" className="element__like">
                        </button>
                        <p className="element__number-likes">{props.likes.length}</p>
                    </div>
                </div>
            </article>
        </>
    )
}

export default Card;
