import React from 'react';
import Header from './Header'
import Main from './Main'
import Footer from './Footer';
import PopupUpdateAvatar from './PopupUpdateAvatar';
import PopupEditProfile from './PopupEditProfile';
import PopupAddCard from './PopupAddCard';
import ImagePopup from './ImagePopup';
import api from "../utils/Api";
import {CurrentUserContext} from "../contexts/CurrentUserContext"


function App() {

  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({ isOpen: false });
  const [currentUser, setCurrentUser] = React.useState('');
  const [cards, setCards] = React.useState([]);


  React.useEffect(() => {
    api.getCardsData()
        .then((data) => {
            setCards(data)
        })
        .catch((err) => {
            console.log(err)
        })
}, [])

function handleCardLike(card) {
  // Снова проверяем, есть ли уже лайк на этой карточке
  const isLiked = card.likes.some(i => i._id === currentUser._id);

  // Отправляем запрос в API и получаем обновлённые данные карточки
  api.setLike(card._id, !isLiked).then((newCard) => {
      setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
  });

  api.deleteLike(card._id, !isLiked).then((newCard) => {
    setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
});
} 




  React.useEffect(()=> {
    api.getUserData()
            .then((data) => {
              setCurrentUser(data)
            })
            .catch((err) => {
                console.log(err)
            })
    }, []
  )


  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true)
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
  }

  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setSelectedCard({ isOpen: false });
  }

  function handleCardClick({ link, name, isOpen }) {
    setSelectedCard({ link, name, isOpen: !isOpen });
  }



  return (
    <CurrentUserContext.Provider value={currentUser}>
    <div className="page">
      <Header />
      <Main onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onEditAvatar={handleEditAvatarClick} onCardClick={handleCardClick} cards={cards} onCardLike={handleCardLike}/>
      <PopupUpdateAvatar isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} buttonText="Сохранить"/>
      <PopupEditProfile isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} buttonText="Сохранить"/>
      <PopupAddCard isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} buttonText="Сохранить"/>
      <ImagePopup card={selectedCard} onClose={closeAllPopups} />
      <Footer />
    </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
